    private async void Awake()
    {
        Debug.Log("Day 13 part 1: " + ExecutePart1(_input));
        //Debug.Log("Day 13 part 2: " + ExecutePart2(_input));
    }

    public static long ExecutePart1(string input)
    {
        string[] groups = input.Split("\r\n\r\n");

        int sum = 0;

        foreach (string group in groups)
        {
            string[] rows = GetRows(group);
            string[] columns = GetColumns(rows);

            for (int i = 1; i < rows.Length; i++)
            {
                if (rows[i] == rows[i - 1])
                {
                    bool isReflection = true;
                    for (int j = 1; j < rows.Length - i; j++)
                    {
                        if (i - 1 - j >= 0 && i + j < rows.Length && rows[i + j] != rows[i - 1 - j])
                        {
                            isReflection = false;
                            break;
                        }
                    }
                    if (isReflection)
                    {
                        sum += 100 * i;
                        break;
                    }
                }
            }
            for (int i = 1; i < columns.Length; i++)
            {
                if (columns[i] == columns[i - 1])
                {
                    bool isReflection = true;
                    for (int j = 1; j < columns.Length - i; j++)
                    {
                        if (i - 1 - j >= 0 && i + j < columns.Length && columns[i + j] != columns[i - 1 - j])
                        {
                            isReflection = false;
                            break;
                        }
                    }
                    if (isReflection)
                    {
                        sum += i;
                        break;
                    }
                }
            }
        }

        return sum;
    }

    public static string[] GetRows(string inputGroup)
    {
        return Common.SplitLines(inputGroup);
    }

    public static string[] GetColumns(string[] rows)
    {
        StringBuilder[] columns = new StringBuilder[rows[0].Length];

        for (int x = 0; x < columns.Length; x++)
        {
            columns[x] = new StringBuilder();
            for (int y = 0; y < rows.Length; y++)
            {
                columns[x].Append(rows[y][x]);
            }
        }

        return columns.Select(c => c.ToString()).ToArray();
    }

