    const char ROLLING_STONE_SYMBOL = 'O';
    const char STATIC_STONE_SYMBOL = '#';
    const char NO_STONE_SYMBOL = '.';

    private async void Awake()
    {
        Debug.Log("Day 13 part 1: " + await Execute(_input, 1));
        Debug.Log("Day 13 part 2: " + await Execute(_input, 1_000_000));
    }

    public static async Task<long> Execute(string input, int cycleCount)
    {
        StringBuilder[] lines = Common.SplitLines(input).Select(s => new StringBuilder(s)).ToArray();

        for (int cycles = 0; cycles < cycleCount; cycles++)
        {
            RollNorth(ref lines);

            if (cycles % 100 == 0)
            {
                Debug.Log($"{cycles} cycles completed");
                await Task.Yield();
            }
        }

        long load = 0;

        for (int i = 0; i < lines.Length; i++)
        {
            for (int x = 0; x < lines[i].Length; x++)
            {
                if (lines[i][x] == ROLLING_STONE_SYMBOL)
                    load += lines.Length - i;
            }
        }

        return load;
    }

    static void RollNorth(ref StringBuilder[] lines)
    {
        bool stonesMoved = false;
        do
        {
            stonesMoved = false;
            for (int i = 1; i < lines.Length; i++)
            {
                for (int x = 0; x < lines[i].Length; x++)
                {
                    if (lines[i][x] == ROLLING_STONE_SYMBOL && lines[i - 1][x] != ROLLING_STONE_SYMBOL && lines[i - 1][x] != STATIC_STONE_SYMBOL)
                    {
                        lines[i - 1][x] = ROLLING_STONE_SYMBOL;
                        lines[i][x] = NO_STONE_SYMBOL;
                        stonesMoved = true;
                    }
                }
            }
        } while (stonesMoved);
    }

}
